import useSWR from 'swr';
import './App.css';

const fetcher = (url: string) => fetch(url).then((res) => res.json());
type Item = {
    id: number;
    title: string;
    body: string;
    done: boolean;
};

function App() {
    const { data, error, mutate } = useSWR(
        'http://localhost:4000/api/todos',
        fetcher
    );
    const deleteTodo = async (id: number) => {
        try {
            const response = await fetch(
                `http://localhost:4000/api/todos/${id}`,
                {
                    method: 'DELETE',
                }
            );
            if (response.ok) {
                mutate();
            } else {
                console.error('Failed to delete the todo item');
            }
        } catch (error) {
            console.error(
                'An error occurred while deleting the todo item',
                error
            );
        }
    };

    return (
        <div className="my-10 mx-10 p-8 shadow-md text-start">
            <h1 className="text-4xl mb-4"> Guide to getting things done. </h1>
            {data?.length > 0 ? (
                data.map((item: Item) => (
                    <div key={item.id} className="mb-8">
                        <h4 className="text-lg font-bold"> {item.title} </h4>
                        <p> {item.body} </p>
                        <button
                            className="py-2 px-4 bg-red-400 text-white rounded-lg mt-2"
                            onClick={() => deleteTodo(item.id)}
                        >
                            Delete
                        </button>
                    </div>
                ))
            ) : (
                <p> No action items yet. </p>
            )}
            <p className="text-right text-sm"> 01. </p>
        </div>
    );
}

export default App;
